import StoryModel from"../model/StoryModel.js";class DetailStoryPresenter{constructor(e){this._view=e,this._model=new StoryModel}async show(e){this._view.showLoading();try{const t=await this._model.getStoryById(e);if(t.error)return void this._view.showError(t.message||"Gagal memuat detail.");const r=t.story||t;this._view.renderDetail(r)}catch(e){console.error(e),this._view.showError("Terjadi kesalahan saat mengambil data.")}}}export default DetailStoryPresenter;